sysauto_script.script:
var currDateSet = new GlideDateTime("2018-10-21");
var maxDaysBack = 7;


	// Get the current starting date for the request
	//var currDateSet = new GlideDate(current.u_starting_date);
	var earliestDatePossible = new GlideDate(gs.daysAgoStart(maxDaysBack));

	if (currDateSet.compareTo(earliestDatePossible) > 0) {
		// If the current date is before the earliest possible date, refuse access
		gs.info("selected date: " + currDateSet.toString() + " , earliest date possible: " + earliestDatePossible.toString() + "error");

	} else {
		gs.info("selected date: " + currDateSet.toString() + " , earliest date possible: " + earliestDatePossible.toString() + "success");
	}
